/*
Opgave 2 
Farvelæg filmtitler ud fra bedømmelser  */

function Farve(){
for (i = 0; i < 3; i++){

	if (filmliste[i].imdbRating < 7) {
		document.getElementById(filmliste[i].imdbID).setAttribute("class", "tekstred");
	}
	else if (filmliste[i].imdbRating >= 7 && filmliste[i].imdbRating  < 8){
		document.getElementById(filmliste[i].imdbID).setAttribute("class", "tekstblue");
	}
	else if (filmliste[i].imdbRating >= 8){
		document.getElementById(filmliste[i].imdbID).setAttribute("class", "tekstgreen");
	}
}
}

