/* Opgave 3/4
Procentuel sammenligning af to lister 
SamL(filmliste[18].Actors, filmliste[19].Actors) giver 25% fordi der er 1 ud af 4 overlap af skuespillere*/



var overLapTæl0 = 0;
function OverLap0(a, b) {
	var a = a.split(",");
	var b = b.split(",");
	for (i = 0; i < 4; i++){
		if (a[i] == b[i]){
		overLapTæl0 += 1
		overLapTæl0 = (overLapTæl0/4)*100
	} else if (overLapTæl0 = 0) {
		document.getElementById(filmliste[1].imdbID).setAttribute("class", "tekstred");
	} 
}
}


